import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  loading: false,
  error: null,
  paymentList: [],
//   pendingPaymentList: [],
};

const payment = createSlice({
  name: 'paymentList',
  initialState,
  reducers: {
    setLoading: (state, action) => {
      state.loading = action.payload;
    },
    setError: (state, action) => {
      state.error = action.payload;
    },
    clearError: (state) => {
      state.error = null;
    },
    setPaymentList: (state, action) => {
      state.paymentList = action.payload;
    },
    // setPendingPaymentList: (state, action) => {
    //   state.pendingPaymentList = action.payload;
    // },
  },
});

export const { setLoading, setError, clearError, setPaymentList } = payment.actions;



export default payment.reducer;
