import { Button, Container, TextField } from "@mui/material";
import { createTheme, ThemeProvider } from "@mui/material/styles";
// import Checkbox from "@mui/material/Checkbox";
// import Stack from "@mui/material/Stack";
import Grid from "@mui/material/Grid";

import Box from "@mui/material/Box";
import { Typography } from "@mui/material";
import MenuItem from "@mui/material/MenuItem";
import Select from "@mui/material/Select";
import FormControl from "@mui/material/FormControl";
import InputLabel from "@mui/material/InputLabel";
import ProtectedRoute from "../../Routes/ProtectedRoute";
import Spinner from "../Spinner/Spinner";
import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { setLoading } from "../../Store/Slices/Invoice";
// import { getInvoicePayload } from "../../CommonComponents/invoicePayload";
import {
  ADD_INVOICE,
  GET_COURSE_FEE,
  GET_INVOICE,
  GET_STUDENTS,
  GET_COURSE_LIST,
  UPDATE_INVOICE,
} from "../../Store/Action_Constants";
import { getInvoicePayload } from "../../CommonComponents/invoicePayload";
import { useNavigate, useParams } from "react-router-dom";
import swal from "sweetalert";
import { Navbar } from "../Navbar/Navbar";
import { Footer } from "../Footer/Footer";
// import moment from "moment";
// import { toast } from "react-toastify";
const theme = createTheme();

export const CreateInvoice = () => {
  const [formData, setFormData] = React.useState({
    selectedStudent: "",
    selectedDuration: "",
    selectedCourse: "",
    selectedCourseFee: "",
    depositeAmount: "",
    pendingAmount: "",
    paymentMethod: "",
    otherPaymentMethod: "",
  });
  console.log(formData.otherPaymentMethod, "4884nn484848");

  const handleChange = async (e) => {
    const { name, value } = e.target;
    console.log(e.target.value,"48484844848", name , value)
    if (name === "selectedDuration") {
      setFormData((prevFormData) => ({
        ...prevFormData,
        [name]: value,
      }));

      console.log("selectedDuration", formData.selectedDuration);

      try {
        dispatch({
          type: GET_COURSE_LIST,
          payload: Number(formData.selectedDuration),
        });
      } catch (error) {
