import React from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableHead from "@mui/material/TableHead";
import Box from "@mui/material/Box";
import Modal from "@mui/material/Modal";
import TableRow from "@mui/material/TableRow";
import CircularProgress from "@mui/material/CircularProgress";
import Button from "@mui/material/Button";
import { payment_list } from "../../Services/Invoice_Services";

const PaymentList = ({ isDepositedModalOpen, onClose, invoiceId }) => {
  const [paymentList, setPaymentList] = React.useState([]);
  const [loading, setLoading] = React.useState(true);

  const paymentListColumns = [
    { id: "amount", label: "Amount", minWidth: 100 },
    { id: "payment_method", label: "Payment Method", minWidth: 100 },
    { id: "created_at", label: "Payment Date", minWidth: 100 },
  ];

  const style = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: 500,
    bgcolor: "background.paper",
    boxShadow: 24,
    p: 4,
  };

  const fetchPaymentList = async () => {
    try {
      setLoading(true);
      const paymentData = await payment_list(invoiceId);
      setPaymentList(paymentData?.data?.paid_amount || []);
    } catch (error) {
      console.error("Error fetching payment list data", error);
    } finally {
      setLoading(false);
    }
  };

  React.useEffect(() => {
    fetchPaymentList();
  }, [invoiceId]);

  const handleRefresh = () => {
    fetchPaymentList();
  };

  return (
    <Modal
      open={isDepositedModalOpen}
      onClose={onClose}
      aria-labelledby="deposited-amounts-modal-title"
      aria-describedby="deposited-amounts-modal-description"
    >
      <Box sx={style}>
        <h2 id="deposited-amounts-modal-title">Deposited Amounts</h2>
        <Box
          sx={{
            display: "flex",
            justifyContent: "flex-end",
            marginBottom: 2,
          }}
        >
          <Button variant="contained" onClick={handleRefresh}>
            Refresh
          </Button>
        </Box>
        {loading ? (
          <Box
            sx={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              justifyContent: "center",
              height: "100%",
            }}
          >
            <CircularProgress />
          </Box>
        ) : (
          <Table>
            <TableHead>
              <TableRow>
                {paymentListColumns.map((column) => (
                  <TableCell key={column.id} align="left">
                    {column.label}
                  </TableCell>
                ))}
              </TableRow>
            </TableHead>
            <TableBody>
              {paymentList.map((paymentItem, index) => (
                <TableRow key={index}>
                  {paymentListColumns.map((column) => {
                    let value = paymentItem[column.id];
                    return (
                      <TableCell key={column.id} align="left">
                        {column.id === "amount" ? (
                          <Box sx={{ display: "flex" }}>
                            <span style={{ marginRight: "4px" }}>Rs</span>
                            {value}
                          </Box>
                        ) : column.id === "payment_method" ? (
                          <Box
                            sx={{
                              display: "flex",
                            }}
                          >
                            {value}
                          </Box>
                        ) : column.id === "created_at" ? (
                          <Box
                            sx={{
                              display: "flex",
                            }}
                          >
                            <>{new Date(value).toLocaleString()}</>
                          </Box>
                        ) : (
                          <>{value}</>
                        )}
                      </TableCell>
                    );
                  })}
                </TableRow>
              ))}
            </TableBody>
          </Table>
        )}
      </Box>
    </Modal>
  );
};

export default PaymentList;
