import { Button, Container, TextField } from "@mui/material";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import Grid from "@mui/material/Grid";
import Box from "@mui/material/Box";
import { Typography } from "@mui/material";
import MenuItem from "@mui/material/MenuItem";
import Select from "@mui/material/Select";
import FormControl from "@mui/material/FormControl";
import InputLabel from "@mui/material/InputLabel";
import ProtectedRoute from "../../Routes/ProtectedRoute";
import Spinner from "../Spinner/Spinner";
import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { setLoading } from "../../Store/Slices/Invoice";
import {
  ADD_INVOICE,
  GET_COURSE_FEE,
  GET_INVOICE,
  GET_STUDENTS,
  GET_COURSE_LIST,
  UPDATE_INVOICE,
  GET_COURSES,
} from "../../Store/Action_Constants";
import { useNavigate, useParams } from "react-router-dom";
import swal from "sweetalert";
import { Navbar } from "../Navbar/Navbar";
import { Footer } from "../Footer/Footer";
// import moment from "moment";
// import { toast } from "react-toastify";
const theme = createTheme();

export const CreateInvoice = () => {
  const { invoice_Id } = useParams();

  const Coursefee = useSelector((state) => state.invoices.fee);

  const [formData, setFormData] = React.useState({
    selectedStudent: "",
    selectedDuration: "",
    selectedCourse: "",
    selectedCourseFee: Coursefee.fee || "",
    depositeAmount: "",
    pendingAmount: "",
    paymentMethod: "",
    otherPaymentMethod: "",
  });
  const courseDuration = useSelector((state) => state.invoices.duration);
  const handleChange = async (e) => {
    const { name, value } = e.target;
    if (name === "selectedDuration") {
      setFormData((prevFormData) => ({
        ...prevFormData,
        [name]: value,
      }));

      console.log("selectedDuration", formData.selectedDuration);

      try {
        dispatch({
          type: GET_COURSE_LIST,
          payload: formData.selectedDuration,
        });
      } catch (error) {
        console.error("Error dispatching action for course fee:", error);
      }
    } else if (name === "selectedCourse") {
      setFormData((prevFormData) => ({
        ...prevFormData,
        [name]: value,
      }));

      try {
        dispatch({
          type: GET_COURSE_FEE,
          payload: value,
        });
      } catch (error) {
        console.error("Error dispatching action for course fee:", error);
      }
    } else if (name === "paymentMethod" && value === "Other") {
      setFormData((prevFormData) => ({
        ...prevFormData,
        [name]: value,
      }));
    } else {
      setFormData((prevFormData) => ({
        ...prevFormData,
        [name]: value,
      }));
    }
  };

  const dispatch = useDispatch();

  const [error, setError] = React.useState({
    studentname: "",
    duration: "",
  });

  const { courses } = useSelector((state) => state.Courses) || {};
  const { students } = useSelector((state) => state.Students);
  const { invoiceCreating, invoiceToUpdate, invoices } = useSelector(
    (state) => state.invoices
  );

  const navigate = useNavigate();
  const { invoiceId } = useParams();

  const isInvoiceCreating = JSON.parse(localStorage.getItem("invoicecreating"));
  React.useEffect(() => {
    if (invoiceId) {
      dispatch(setLoading(true));
      dispatch({ type: GET_INVOICE, payload: invoiceId });
    }
  }, []);


  React.useEffect(() => {
    if (
      !invoiceCreating &&
      (!isInvoiceCreating || isInvoiceCreating === "false")
    ) {
      navigate("/invoices");
    }
  }, [invoiceCreating]);

  React.useEffect(() => {
    dispatch({ type: GET_STUDENTS, dropdown: 1 });
  }, []);



  const renderStudents = () => {
    return students.map((student, ind) => {
      return (
        <MenuItem value={student.id} key={ind}>
          {student?.name}
        </MenuItem>
      );
    });
  };
  const renderDuration = () => {
    return courses.map((course, ind) => {
      return (
        <MenuItem value={course?.duration} key={ind} data-id={course?.id}>
          {course?.duration}
        </MenuItem>
      );
    });
  };
  React.useEffect(() => {
    dispatch({type: GET_COURSES});
  }, [dispatch]);
  const renderCourse = () => {
    const courseId = courseDuration?.[0]?.course_id;
    const data = courseDuration?.[0]?.course_name?.map((course, i) => {
      return (
        <MenuItem value={courseId[i]} key={courseId[i]}>
          {course}
        </MenuItem>
      );
    });
    return data;
  };


  const handleSubmit = (event) => {
    event.preventDefault();
    const { selectedStudent, selectedCourse, depositeAmount, paymentMethod } =
      formData;
    const data = {
      id: invoice_Id,
      student_id: selectedStudent,
      course_id: selectedCourse,
      deposited_amount: depositeAmount,
      payment_method: paymentMethod,
    };
    dispatch(setLoading(true));

    const formDataPayload = new FormData();
    formDataPayload.append("student_id", selectedStudent);
    formDataPayload.append("course_id", selectedCourse);
    formDataPayload.append("deposited_amount", depositeAmount);
    formDataPayload.append("payment_method", paymentMethod);

    if (invoice_Id) {
      formDataPayload.append("id", invoice_Id);
      dispatch({
        type: UPDATE_INVOICE,
        payload: data,
      });
      return;
    }

    dispatch({
      type: ADD_INVOICE,
      payload: formDataPayload,
    });
  };
  React.useEffect(() => {
    if (invoiceToUpdate && invoiceId) {
      setFormData({
        selectedStudent: invoiceToUpdate.student_id || "",
        selectedDuration: invoiceToUpdate.duration || "",
        selectedCourse: invoiceToUpdate.course_id || "",
        selectedCourseFee: invoiceToUpdate.course_fee || "",
        depositeAmount: invoiceToUpdate.deposited_amount || "",
        pendingAmount: invoiceToUpdate.pending_amount || "",
        paymentMethod: invoiceToUpdate.payment_method || "",
        otherPaymentMethod: invoiceToUpdate.other_payment_method || "",
      });
    }
  }, [invoiceToUpdate]);

  return (
    <ProtectedRoute>
      <Navbar />
      <Spinner loading={false} />
      <ThemeProvider theme={theme}>
        <Container
          component="main"
          sx={{ marginTop: 8, display: "flex", justifyContent: "center" }}
          className="min_height create_invoice"
        >
          <Box
            sx={{
              marginTop: 4,
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              width: "40%",
            }}
          >
            <Box
              sx={{
                width: "100%",
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
              }}
            >
              <Grid container spacing={1}>
                {/* First column */}
                <Grid item xs={6}>
                  <FormControl sx={{ width: "100%", marginTop: 2 }} required>
                    <InputLabel id="demo-simple-select-label">
                      Select Student
                    </InputLabel>
                    <Select
                      name="selectedStudent"
                      labelId="selected_student"
                      id="selected_student"
                      value={formData.selectedStudent}
                      label="selected_student"
                      onChange={handleChange}
                    >
                      {renderStudents()}
                    </Select>
                  </FormControl>
                  {error.studentname && (
                    <Typography className="emailError">
                      {error.studentname}
                    </Typography>
                  )}
                </Grid>

                {/* Second column */}
                <Grid item xs={6}>
                  <FormControl sx={{ width: "100%", marginTop: 2 }} required>
                    <InputLabel id="demo-simple-select-label">
                      Course Duration
                    </InputLabel>
                    <Select
                      name="selectedDuration"
                      labelId="duration"
                      id="duration"
                      value={formData?.selectedDuration}
                      label="duration_type"
                      onChange={handleChange}
                    >
                      {renderDuration()}
                    </Select>
                  </FormControl>
                  {error.duration && (
                    <Typography className="emailError">
                      {error.duration}
                    </Typography>
                  )}
                </Grid>
              </Grid>
            </Box>

            <FormControl sx={{ width: "100%", marginTop: 2 }} required>
              <InputLabel id="demo-simple-select-label">Course</InputLabel>
              <Select
                name="selectedCourse"
                labelId="course_type"
                id="course_type"
                value={formData?.selectedCourse}
                label="course_type"
                onChange={handleChange}
              >
                {renderCourse()}
              </Select>
            </FormControl>
            <FormControl sx={{ width: "100%", marginTop: 2 }} required>
              <InputLabel id="demo-simple-select-label">Course Fee</InputLabel>
              {console.log(Coursefee.fee, "ifififffif")}
              <Select
                labelId="coursefee_type"
                id="coursefee_type"
                name="selectedCourseFee"
                value={Coursefee.fee}
                label="coursefee_type"
                onChange={handleChange}
                // disabled={formData?.selectedCourseFee.length<0?true:false}
              >
                {console.log(
                  Coursefee.fee,
                  "depositedddAmount",
                  Number(formData.depositeAmount)
                )}
                <MenuItem value={Coursefee.fee}>{Coursefee.fee}</MenuItem>
              </Select>
            </FormControl>
            <Grid container spacing={1}>
              <Grid item xs={6}>
                <Box sx={{ width: "100%", marginTop: 2 }}>
                  <TextField
                    required
                    fullWidth
                    type="number"
                    id="name"
                    label="Deposite Amount"
                    name="depositeAmount"
                    autoComplete="name"
                    inputProps={{ sx: { height: 10, marginTop: 1 } }}
                    onChange={handleChange}
                    value={formData.depositeAmount}
                  />
                </Box>
              </Grid>
              <Grid item xs={6}>
                <Box sx={{ width: "100%", marginTop: 2 }}>
                  <TextField
                    required
                    type="number"
                    fullWidth
                    id="name"
                    label="Pending Amount"
                    name="pendingAmount"
                    autoComplete="name"
                    inputProps={{ sx: { height: 10, marginTop: 1 } }}
                    onChange={handleChange}
                    value={Coursefee.fee - formData.depositeAmount}
                  />
                </Box>
              </Grid>
            </Grid>

            <FormControl sx={{ width: "100%", marginTop: 2 }} required>
              <InputLabel id="demo-simple-select-label">
                Payment Method
              </InputLabel>
              <Select
                labelId="payment_type"
                id="payment_type"
                name="paymentMethod"
                value={formData.paymentMethod}
                label="payment_type"
                onChange={handleChange}
              >
                <MenuItem value="Google Pay">Google Pay</MenuItem>
                <MenuItem value="Phone Pay">Phone Pay</MenuItem>
                <MenuItem value="Net Banking">Net Banking</MenuItem>
                <MenuItem value="Credit card">Credit Card</MenuItem>
                <MenuItem value="Debit Card">Debit Card</MenuItem>
                <MenuItem value="Other">Other</MenuItem>
              </Select>
            </FormControl>
            {formData.paymentMethod === "Other" && (
              <FormControl sx={{ width: "100%", marginTop: 2 }} required>
                <TextField
                  required
                  type="text"
                  fullWidth
                  id="otherPaymentMethod"
                  label="Other Payment Method"
                  name="otherPaymentMethod"
                  autoComplete="otherPaymentMethod"
                  inputProps={{ sx: { height: 10, marginTop: 1 } }}
                  value={formData.otherPaymentMethod}
                  onChange={handleChange}
                />
              </FormControl>
            )}
            <Box className="task_form" component="form" onSubmit={handleSubmit}>
              <Box mt={2} sx={{ width: "87%" }}>
                <Button
                  type="submit"
                  sx={{ width: "100%", marginBottom: 1.5 }}
                  variant="contained"
                >
                  {invoice_Id ? "Update Invoice" : "Create Invoice"}

                  {/* Create Invoice */}
                </Button>
              </Box>
            </Box>
          </Box>
        </Container>
      </ThemeProvider>
      <Footer />
    </ProtectedRoute>
  );
};
