import React from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableHead from "@mui/material/TableHead";
import Box from "@mui/material/Box";
import Modal from "@mui/material/Modal";
import TableRow from "@mui/material/TableRow";
import CircularProgress from "@mui/material/CircularProgress";
import Button from "@mui/material/Button";
import { payment_list } from "../../Services/Invoice_Services";

const PaymentList = ({ isDepositedModalOpen, onClose, invoiceId }) => {
  const [paymentList, setPaymentList] = React.useState([]);
  const [loading, setLoading] = React.useState(true);
  const [isEditMode, setIsEditMode] = React.useState(false);
  const [editedData, setEditedData] = React.useState({});
  const [editableField, setEditableField] = React.useState(null);

  const paymentListColumns = [
    { id: "id", label: "InvoiceId", minWidth: 100 },
    { id: "created_at", label: "Payment Date", minWidth: 100 },
    { id: "amount", label: "Amount", minWidth: 100 },
    { id: "payment_method", label: "Payment Method", minWidth: 100 },
  ];

  const style = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: 700,
    bgcolor: "background.paper",
    boxShadow: 24,
    p: 4,
  };

  React.useEffect(() => {
    const fetchPaymentList = async () => {
      try {
        setLoading(true);
        const paymentData = await payment_list(invoiceId);
        setPaymentList(paymentData?.data?.paid_amount || []);
      } catch (error) {
        console.error("Error fetching payment list data", error);
      } finally {
        setLoading(false);
      }
    };

    fetchPaymentList();
  }, [invoiceId]);

  const toggleEditMode = () => {
    setIsEditMode(!isEditMode);
  };

  const handleEdit = (value, columnId, index) => {
    if (editableField === columnId) {
      setEditedData({
        ...editedData,
        [index]: {
          ...editedData[index],
          [columnId]: value,
        },
      });
    }
  };

  const applyEdits = () => {
    // You may want to handle the edited data here, e.g., send it to the server
    // For simplicity, we're just updating the state in this example
    setPaymentList((prevPaymentList) =>
      prevPaymentList.map((item, index) => ({
        ...item,
        ...editedData[index],
      }))
    );
    toggleEditMode();
    setEditableField(null); // Reset editable field after applying edits
  };

  const renderCellContent = (column, value, index) => {
    if (isEditMode && editableField === column.id) {
      return (
        <input
          type="text"
          value={editedData[index]?.[column.id] || value}
          onChange={(e) => handleEdit(e.target.value, column.id, index)}
        />
      );
    } else if (column.id === "created_at") {
      return (
        <Box sx={{ display: "flex" }}>
          <span style={{ marginRight: "4px" }}>Rs</span>
          {new Date(value).toLocaleString()}{" "}
        </Box>
      );
    } else {
      return <>{value}</>;
    }
  };

  return (
    <Modal
      open={isDepositedModalOpen}
      onClose={onClose}
      aria-labelledby="deposited-amounts-modal-title"
      aria-describedby="deposited-amounts-modal-description"
    >
      <Box sx={style}>
        <h2 id="deposited-amounts-modal-title">Payment History</h2>
        {loading ? (
          <Box
            sx={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              justifyContent: "center",
              height: "100%",
            }}
          >
            <CircularProgress />
          </Box>
        ) : (
          <Table>
            <TableHead>
              <TableRow>
                {paymentListColumns.map((column) => (
                  <TableCell key={column.id} align="left">
                    {column.label}
                  </TableCell>
                ))}
                <TableCell align="left">Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {paymentList.map((paymentItem, index) => (
                <TableRow key={index}>
                  {paymentListColumns.map((column) => (
                    <TableCell key={column.id} align="left">
                      {renderCellContent(column, paymentItem[column.id], index)}
                    </TableCell>
                  ))}
                  <TableCell align="left">
                    {isEditMode ? (
                      <Button onClick={() => applyEdits()}>Apply</Button>
                    ) : (
                      <Button onClick={() => setEditableField("amount")}>Edit Amount</Button>
                    )}
                    <Button onClick={() => setEditableField("payment_method")}>Edit Method</Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        )}
      </Box>
    </Modal>
  );
};

export default PaymentList;
