// PaymentModal.js
import React from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import { FormControl, Grid, InputLabel, MenuItem, Select, TextField } from '@mui/material';

const style = {
    position: 'absolute',
    top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};

const PaymentModal = ({ isOpen, onClose, onPayment }) => {
  return (
    <Modal
      open={isOpen}
      onClose={onClose}
      aria-labelledby="payment-modal-title"
      aria-describedby="payment-modal-description"
    >
      <Box sx={style}>
      <Grid item xs={12}>
                <Box sx={{ width: "100%", marginTop: 2 }}>
                  <TextField
                    required
                    type="number"
                    fullWidth
                    id="name"
                    label="Pending Amount"
                    name="pendingAmount"
                    autoComplete="name"
                    inputProps={{ sx: { height: 10, marginTop: 1 } }}
                    // onChange={handleChange}
                    // value={formData.pendingAmount}
                  />
                </Box>
              </Grid>
      <FormControl sx={{ width: "100%", marginTop: 2 }} required>
              <InputLabel id="demo-simple-select-label">
                Payment Method
              </InputLabel>
              <Select
                labelId="payment_type"
                id="payment_type"
                name="paymentMethod"
                // value={formData.paymentMethod}
                label="payment_type"
                // onChange={handleChange}
              >
                {/* {paymentMethods.map((method) => ( */}
                  <MenuItem value='google pay'>
                    Google pay
                  </MenuItem>
                {/* ))} */}
              </Select>
            </FormControl>
            <Box className="task_form" component="form" >
              <Box mt={2} sx={{ width: "87%" }}>
                <Button
                  type="submit"
                  sx={{ width: "100%", marginBottom: 1.5 }}
                  variant="contained"
                >
                    Pay
                </Button>
              </Box>
            </Box>
        </Box>
    </Modal>
  );
};

export default PaymentModal;
