import React from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableHead from "@mui/material/TableHead";
import Box from "@mui/material/Box";
import Modal from "@mui/material/Modal";
import TableRow from "@mui/material/TableRow";
import CircularProgress from "@mui/material/CircularProgress";
import Button from "@mui/material/Button";
import { TextField } from "@mui/material";
import { payment_list } from "../../Services/Invoice_Services";
import { useDispatch, useSelector } from "react-redux";
import { PAYMENT_LIST, UPDATE_PAYMENT } from "../../Store/Action_Constants";

const PaymentList = ({ isDepositedModalOpen, onClose, invoiceId }) => {
  const dispatch = useDispatch()
  const [paymentList, setPaymentList] = React.useState([]);
  const [loading, setLoading] = React.useState(true);
  const [editableRow, setEditableRow] = React.useState(null);
  const [editedData, setEditedData] = React.useState({});
  const paymentListdata = useSelector((state) => state.Payment.paymentList);
console.log(paymentListdata, "4566")
  const paymentListColumns = [
    { id: "id", label: "InvoiceId", minWidth: 100 },
    { id: "created_at", label: "Payment Date", minWidth: 100 },
    { id: "amount", label: "Amount", minWidth: 100 },
    { id: "payment_method", label: "Payment Method", minWidth: 100 },
  ];

  const style = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: 800,
    bgcolor: "background.paper",
    boxShadow: 24,
    p: 4,
    overflowY: "auto",
    maxHeight: "60vh",
  };

  // const fetchPaymentList = async () => {
  //   try {
  //     setLoading(true);
  //     const paymentData = await payment_list({"id":invoiceId});
  //     setPaymentList(paymentData?.data?.paid_amount || []);
  //   } catch (error) {
  //     console.error("Error fetching payment list data", error);
  //   } finally {
  //     setLoading(false);
  //   }
  // };

  const toggleEditMode = (rowIndex) => {
    setEditableRow(editableRow === rowIndex ? null : rowIndex);
    if (editableRow !== rowIndex) {
      setEditedData({});
    }
  };

  const handleEdit = (value, columnId, index) => {
    if (columnId === "amount" || columnId === "payment_method") {
      setEditedData({
        ...editedData,
        [index]: {
          ...editedData[index],
          [columnId]: value,
        },
      });
    }
  };

  const applyEdits = async () => {
    try {
      for (const index in editedData) {
        const editedItem = editedData[index];
        const paymentItem = paymentList[index];

        const updatedData = {
          id: paymentItem.id, 
          amount: editedItem.amount || paymentItem.amount,
          payment_method: editedItem.payment_method || paymentItem.payment_method,
        };
        dispatch({ type: UPDATE_PAYMENT, payload: updatedData });
      }
      // await fetchPaymentList();
      setEditedData({});
      toggleEditMode(null);
    } catch (error) {
      console.error("Error updating payment data", error);
    }
  };

  React.useEffect(() => {
    // fetchPaymentList();
    dispatch({type:PAYMENT_LIST , payload : invoiceId})
  }, [invoiceId, dispatch]);

  return (
    <Modal
      open={isDepositedModalOpen}
      onClose={onClose}
      aria-labelledby="deposited-amounts-modal-title"
      aria-describedby="deposited-amounts-modal-description"
    >
      <Box sx={style}>
        <h2 id="deposited-amounts-modal-title">Payment History</h2>
        {loading ? (
          <Box
            sx={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              justifyContent: "center",
              height: "100%",
            }}
          >
            <CircularProgress />
          </Box>
        ) : (
          <Table>
            <TableHead>
              <TableRow>
                {paymentListColumns.map((column) => (
                  <TableCell key={column.id} align="left">
                    {column.label}
                  </TableCell>
                ))}
                <TableCell align="left">Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {paymentList.map((paymentItem, index) => (
                <TableRow key={index}>
                  {paymentListColumns.map((column) => (
                    <TableCell key={column.id} align="left">
                      {editableRow === index &&
                      (column.id === "amount" ||
                        column.id === "payment_method") ? (
                        <TextField
                          type="text"
                          value={
                            editedData[index]?.[column.id] !== undefined
                              ? editedData[index]?.[column.id]
                              : paymentItem[column.id]
                          }
                          onChange={(e) =>
                            handleEdit(e.target.value, column.id, index)
                          }
                          size="small"
                        />
                      ) : column.id === "created_at" ? (
                        <Box sx={{ display: "flex" }}>
                          <span style={{ marginRight: "4px" }}>Rs</span>
                          {new Date(
                            paymentItem[column.id]
                          ).toLocaleString()}{" "}
                        </Box>
                      ) : (
                        <>{paymentItem[column.id]}</>
                      )}
                    </TableCell>
                  ))}
                  <TableCell align="left">
                    {editableRow === index ? (
                      <Button onClick={() => applyEdits()}>Apply</Button>
                    ) : (
                      <Button onClick={() => toggleEditMode(index)}>
                        Edit
                      </Button>
                    )}
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        )}
      </Box>
    </Modal>
  );
};

export default PaymentList;
