import swal from "sweetalert";
import { Navbar } from "../Navbar/Navbar";
import { Footer } from "../Footer/Footer";
// import moment from "moment";
// import { toast } from "react-toastify";
const theme = createTheme();

export const CreateInvoice = () => {
  const dispatch = useDispatch();


  const [error, setError] = React.useState({
    studentname: "",
    duration: "",
  });

  const { students } = useSelector((state) => state.Students);
  const { invoiceCreating, invoiceToUpdate } = useSelector(
    (state) => state.invoices
  );
  console.log("invoiceToUpdate, invoiceCreating ",invoiceToUpdate, invoiceCreating)
  const [selectedStudent, setSelectedStudent] = React.useState({
    id: "",
    index: "",
    name: "",
  });
  const [selectedDuration, setSelectedDuration] = React.useState({
    id: "",
    index: "",
    duration: "",
  });
  const [selectedCourse, setSelectedCourse] = React.useState({
    id: "",
    index: "",
    course: "",
  });
  const [selectedCourseFee, setSelectedCourseFee] = React.useState({
    id: "",
    index: "",
    fee: "",
  });

  const [depositeAmount, setDepositeAmount] = React.useState(0)
  const [pendingAmount, setPendingAmount] = React.useState(0)
  const [paymentMethod, setPaymentMethod] = React.useState("")

