// PaymentModal.js
import React from "react";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import Modal from "@mui/material/Modal";
import {
  FormControl,
  Grid,
  InputLabel,
  MenuItem,
  Select,
  TextField,
} from "@mui/material";

const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 400,
  bgcolor: "background.paper",
  //   border: "2px solid #000",
  boxShadow: 24,
  p: 4,
};
const paymentMethods = [
  "Google pay",
  "Phone pay",
  "Credit card",
  "Debit card",
  "Net banking",
  "Other",
]; // Add your payment methods here

const PaymentModal = ({ isOpen, onClose, onPayment, invoiceData }) => {
  const [paymentMethod, setPaymentMethod] = useState(paymentMethod[0]);
  const [pendingAmount, setPendingAmount] = useState("");
  return (
    <Modal
      open={isOpen}
      onClose={onClose}
      aria-labelledby="payment-modal-title"
      aria-describedby="payment-modal-description"
    >
      <Box sx={style}>
        <FormControl sx={{ width: "100%", marginTop: 2 }} required>
          <InputLabel id="demo-simple-select-label">Payment Method</InputLabel>
          <Select
            labelId="payment_type"
            id="payment_type"
            name="paymentMethod"
            label="payment_type"
            value={paymentMethod}
            onChange={handleChange}
          >
            {paymentMethods.map((method) => (
              <MenuItem key={method} value={method}>
                {method}
              </MenuItem>
            ))}{" "}
          </Select>
        </FormControl>
        <Grid item xs={12}>
          <Box sx={{ width: "100%", marginTop: 2 }}>
            <TextField
              required
              type="number"
              fullWidth
              id="name"
              label="Pending Amount"
              name="pendingAmount"
              autoComplete="name"
              inputProps={{ sx: { height: 10, marginTop: 1 } }}
            />
          </Box>
        </Grid>

        <Box className="task_form" component="form" onSubmit={onPayment}>
          <Box mt={2} sx={{ width: "87%" }}>
            <Button
              type="submit"
              sx={{ width: "100%", marginBottom: 1.5 }}
              variant="contained"
            >
              Pay
            </Button>
          </Box>
        </Box>
      </Box>
    </Modal>
  );
};

export default PaymentModal;
