import * as React from "react";
import Button from "@mui/material/Button";
import CssBaseline from "@mui/material/CssBaseline";
import TextField from "@mui/material/TextField";
import Grid from "@mui/material/Grid";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import ProtectedRoute from "../../Routes/ProtectedRoute";
import { getClientPayload } from "../../CommonComponents/clientPayload";
import { toast } from "react-toastify";
import { useDispatch, useSelector } from "react-redux";
import { setLoading } from "../../Store/Slices/Clients";
import {
  GET_CLIENT,
  SAVE_CLIENT,
  UPDATE_CLIENT,
} from "../../Store/Action_Constants";
import Spinner from "../Spinner/Spinner";
import { useNavigate, useParams } from "react-router-dom";
import { Navbar } from "../Navbar/Navbar";
import { Footer } from "../Footer/Footer";
import TextareaAutosize from "@mui/base/TextareaAutosize";
const theme = createTheme();

export default function Add_Client() {
  const dispatch = useDispatch();
  const { id } = useParams();
  const { loading, clientCreating, clients, client } = useSelector(
    (state) => state.clients
  );
  const navigate = useNavigate();
  const isClientCreating = JSON.parse(localStorage.getItem("clientcreating"));
  const [clientInfo, setClientInfo] = React.useState({
    name: "",
    address: "",
    phone: "",
    pan: "",
    companyname: "",
    gstin: "",
    pin: "",
    tan: "",
    email: "",
  });
  const [error, setError] = React.useState({ email: "", phone: "" });

  const handleSubmit = (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    const payload = getClientPayload(data);
    console.log(error, "error");
    if (!payload.name || !payload.address || !payload.email || !payload.phone) {
      toast.error("Please fill all the required fields", {
        toastId: "sender_form",
      });
      return;
    }
    if (error.email) {
      toast.error("Please Enter valid Email");
      return;
    }
    if (error.phone) {
      toast.error("Phone Number must have 10 digits");
      return;
    }
    dispatch(setLoading(true));

    if (id) {
      payload.id = clientInfo.id;
      dispatch({
        type: UPDATE_CLIENT,
        payload: payload,
      });
      return;
    }

    dispatch({
      type: SAVE_CLIENT,
      payload: payload,
    });
  };

  React.useEffect(() => {
    if (id) setClientInfo(client);
  }, [client]);

  React.useEffect(() => {
    if (id) {
      dispatch(setLoading(true));
      dispatch({ type: GET_CLIENT, payload: id });
    }
  }, []);

  React.useEffect(() => {
    if (
      !clientCreating &&
      (!isClientCreating || isClientCreating === "false")
    ) {
      navigate("/clients");
    }
  }, [clientCreating]);

  const isValidEmail = (email) => {
    return /\S+@\S+\.\S+/.test(email);
  };

  const handleInput = (e) => {
    console.log("heellllo", e.target.value);

    if (e.target.name === "email") {
      if (e.target.value !== "" && !isValidEmail(e.target.value)) {
        setError({ ...error, email: "Email is invalid" });
      } else {
        setError({ ...error, email: "" });
      }
    }
    if (e.target.name === "phone") {
      if (
        e.target.value !== "" &&
        (e.target.value.length > 10 || e.target.value.length < 10)
      ) {
        setError({
          ...error,
          phone: "Phone number must be atleast 10 numbers",
        });
      } else {
        setError({ ...error, phone: "" });
      }
    }
    setClientInfo({ ...clientInfo, [e.target.name]: e.target.value });
  };

  return (
    <ProtectedRoute>
      <Navbar />
      <Spinner loading={loading} />
      <ThemeProvider theme={theme}>
        <Container component="main" maxWidth="sm" className="main-container">
          <CssBaseline />
          <Box
            sx={{
              marginTop: 16,
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
            }}
          >
            <Typography component="h1" variant="h5">
              {id ? "Update Client" : "Add Client"}
            </Typography>
            <Box
              component="form"
              noValidate
              onSubmit={handleSubmit}
              sx={{ mt: 3 }}
            >
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    fullWidth
                    id="name"
                    label="NAME"
                    name="name"
                    autoComplete="name"
                    inputProps={{ sx: { height: 10, marginTop: 1 } }}
                    onChange={handleInput}
                    value={clientInfo.name}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    fullWidth
                    id="companyname"
                    label="COMPANY NAME "
                    name="companyname"
                    autoComplete="companyname"
                    inputProps={{ sx: { height: 10, marginTop: 1 } }}
                    onChange={handleInput}
                    value={clientInfo.companyname}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    aria-label="minimum height"
                    id="address"
                    multiline
                    maxRows={3}
                    label="ADDRESS *"
                    autoComplete="address"
                    name="address"
                    inputProps={{ sx: { height: 10 } }}
                    style={{ width: 540 }}
                    onChange={handleInput}
                    value={clientInfo.address}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    id="email"
                    label="Email Address *"
                    name="email"
                    autoComplete="email"
                    inputProps={{ sx: { height: 10, marginTop: 1 } }}
                    onChange={handleInput}
                    value={clientInfo.email}
                  />
                  <Typography className="errorStyle">{error.email}</Typography>
                </Grid>

                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    fullWidth
                    id="phone"
                    label="PHONE "
                    name="phone"
                    type="number"
                    autoComplete="phone"
                    inputProps={{ sx: { height: 10, marginTop: 1 } }}
                    onChange={handleInput}
                    value={clientInfo.phone}
                  />
                  <Typography className="errorStyle">{error.phone}</Typography>
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    id="pan"
                    label="PAN NUMBER *"
                    name="pan"
                    autoComplete="pan"
                    inputProps={{ sx: { height: 10, marginTop: 1 } }}
                    onChange={handleInput}
                    value={clientInfo.pan}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    id="gstin"
                    label="GSTIN *"
                    name="gstin"
                    autoComplete="gstin"
                    inputProps={{ sx: { height: 10, marginTop: 1 } }}
                    onChange={handleInput}
                    value={clientInfo.gstin}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    id="pin"
                    label="PIN NUMBER *"
                    name="pin"
                    autoComplete="pin"
                    inputProps={{ sx: { height: 10, marginTop: 1 } }}
                    onChange={handleInput}
                    value={clientInfo.pin}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    id="tan"
                    label="TAN *"
                    name="tan"
                    autoComplete="tan"
                    inputProps={{ sx: { height: 10, marginTop: 1 } }}
                    onChange={handleInput}
                    value={clientInfo.tan}
                  />
                </Grid>
              </Grid>
              <Box mt={3}>
                <Button
                  type="submit"
                  fullWidth
                  variant="contained"
                  onSubmit={handleSubmit}
                  sx={{ mb: 2, backgroundColor: "#1976d2" }}
                >
                  {id ? "Update Client" : "Save Client"}
                </Button>
              </Box>
            </Box>
          </Box>
        </Container>
      </ThemeProvider>
      <Footer />
    </ProtectedRoute>
  );
}
